create materialized view if not exists mv142 as select cast_info.note AS note, cast_info.person_role_id AS person_role_id, char_name.name AS name, company_name.country_code AS country_code, company_name.id AS company_name_id_4, company_type.id AS company_type_id_5, title.production_year AS production_year, title.title AS title
from cast_info, char_name, company_name, company_type, movie_companies, role_type, title
 where (cast_info.movie_id = movie_companies.movie_id) And (cast_info.note like '%(producer)%') And (cast_info.role_id = role_type.id) And (char_name.id = cast_info.person_role_id) And (company_name.country_code = '[us]') And (company_type.id = movie_companies.company_type_id) And (movie_companies.company_id = company_name.id) And (movie_companies.movie_id = cast_info.movie_id) And (role_type.id = cast_info.role_id) And (title.id = movie_companies.movie_id)