create materialized view if not exists mv153 as select link_type.id AS id, link_type.link AS link, movie_companies.company_id AS company_id, movie_companies.company_type_id AS company_type_id, movie_companies.movie_id AS movie_companies_movie_id_4, movie_companies.note AS note, movie_keyword.keyword_id AS keyword_id, movie_link.movie_id AS movie_link_movie_id_7, title.production_year AS production_year, title.title AS title
 from movie_keyword,movie_link,title,movie_companies,link_type
 where (title.id = movie_keyword.movie_id) And (title.id = movie_link.movie_id) And (movie_link.link_type_id = link_type.id) And (movie_companies.movie_id = title.id) And (title.title like '%Money%')  And  (title.production_year = 1998) And (movie_companies.note IS NULL) And (link_type.link like '%follows%')