create materialized view if not exists mv159 as select company_name.country_code AS country_code, company_name.id AS company_name_id_1, company_name.name AS name, keyword.id AS keyword_id_3, keyword.keyword AS keyword, movie_companies.company_type_id AS company_type_id, movie_companies.movie_id AS movie_id, movie_companies.note AS note
 from movie_companies,company_name,movie_keyword,keyword
 where (movie_keyword.movie_id = movie_companies.movie_id) And (movie_companies.company_id = company_name.id) And (movie_companies.note IS NOT NULL) And (company_name.country_code = '__NOTEQUAL__[pl]')  And  (((company_name.name = '__LIKE__20th Century Fox%')) Or ((company_name.name = '__LIKE__Twentieth Century Fox%'))) And (movie_keyword.keyword_id = keyword.id) And (keyword.keyword in ('sequel', 'revenge', 'based-on-novel'))