create materialized view if not exists mv163 as select company_name.country_code AS country_code, company_name.id AS company_name_id_1, company_name.name AS name, company_type.id AS company_type_id_3, company_type.kind AS kind, keyword.id AS keyword_id_5, keyword.keyword AS keyword, link_type.id AS link_type_id_7, movie_companies.movie_id AS movie_id, movie_companies.note AS note, title.production_year AS production_year, title.title AS title
 from company_name,movie_keyword,movie_link,company_type,keyword,title,movie_companies,link_type
 where (movie_companies.company_id = company_name.id) And (company_name.country_code = '__NOTEQUAL__[pl]')  And  (((company_name.name = '__LIKE__20th Century Fox%')) Or ((company_name.name = '__LIKE__Twentieth Century Fox%'))) And (title.id = movie_keyword.movie_id) And (movie_keyword.movie_id = movie_companies.movie_id) And (movie_keyword.movie_id = movie_link.movie_id) And (movie_keyword.keyword_id = keyword.id) And (movie_link.link_type_id = link_type.id) And (movie_link.movie_id = title.id) And (movie_companies.company_type_id = company_type.id) And (company_type.kind IS NOT NULL)  And  (company_type.kind = '__NOTEQUAL__production companies') And (keyword.keyword in ('sequel', 'revenge', 'based-on-novel')) And (movie_companies.note IS NOT NULL)