create materialized view if not exists mv166 as select keyword.id AS id, keyword.keyword AS keyword, movie_companies.company_id AS company_id, movie_companies.company_type_id AS company_type_id, movie_companies.movie_id AS movie_id, movie_companies.note AS note, movie_link.link_type_id AS link_type_id, title.production_year AS production_year, title.title AS title
from keyword, movie_companies, movie_keyword, movie_link, title
 where (keyword.keyword in ('sequel', 'revenge', 'based-on-novel')) And (movie_companies.movie_id = movie_keyword.movie_id) And (movie_companies.note IS NOT NULL) And (movie_keyword.keyword_id = keyword.id) And (movie_link.movie_id = movie_keyword.movie_id) And (title.id = movie_link.movie_id) And (title.production_year > 1950)