create materialized view if not exists mv245 as select cast_info.movie_id AS cast_info_movie_id_0, cast_info.person_id AS person_id, company_name.country_code AS country_code, company_name.id AS company_name_id_3, keyword.id AS keyword_id_4, keyword.keyword AS keyword, movie_companies.movie_id AS movie_companies_movie_id_6, title.episode_nr AS episode_nr, title.title AS title
from cast_info, company_name, keyword, movie_companies, movie_keyword, title
 where (cast_info.movie_id = title.id) And (company_name.country_code = '[us]') And (keyword.keyword = 'character-name-in-title') And (movie_companies.company_id = company_name.id) And (movie_keyword.keyword_id = keyword.id) And (movie_keyword.movie_id = movie_companies.movie_id) And (title.id = movie_keyword.movie_id)