create materialized view if not exists mv271 as select cast_info.movie_id AS movie_id, cast_info.note AS cast_info_note_1, cast_info.person_id AS person_id, info_type.id AS id, info_type.info AS info_type_info_4, movie_info.info AS movie_info_info_5, movie_info.info_type_id AS info_type_id, movie_info.note AS movie_info_note_7, movie_info_idx.info AS movie_info_idx_info_8, name.gender AS gender
 from name,cast_info,movie_info_idx,movie_info,info_type
 where (name.id = cast_info.person_id) And (cast_info.movie_id = movie_info.movie_id) And (cast_info.note in ('(writer)', '(head writer)', '(written by)', '(story)', '(story editor)')) And (movie_info.movie_id = movie_info_idx.movie_id) And (movie_info_idx.info_type_id = info_type.id) And (movie_info_idx.info > '8.0') And (movie_info.info_type_id = info_type.id) And (movie_info.note IS NULL)  And  (movie_info.info in ('Horror', 'Thriller')) And (info_type.info = 'genres') And (info_type.info = 'rating')