create materialized view if not exists mv368 as select info_type.id AS info_type_id_0, info_type.info AS info_type_info_1, kind_type.id AS kind_type_id_2, kind_type.kind AS kind, movie_companies.company_id AS company_id, movie_companies.company_type_id AS company_type_id, movie_companies.movie_id AS movie_id, movie_info.info AS movie_info_info_7, movie_info.note AS note, movie_keyword.keyword_id AS keyword_id, title.production_year AS production_year, title.title AS title
 from movie_keyword,title,kind_type,movie_info,movie_companies,info_type
 where (movie_keyword.movie_id = title.id) And (title.id = movie_info.movie_id) And (movie_companies.movie_id = title.id) And (kind_type.id = title.kind_id) And (movie_info.info_type_id = info_type.id) And (movie_info.note like '%internet%')  And  (movie_info.info like 'USA:% 200%') And (info_type.info = 'release dates')