create materialized view if not exists mv434 as select comp_cast_type.id AS comp_cast_type_id_0, comp_cast_type.kind AS kind, company_name.country_code AS country_code, company_name.id AS company_name_id_3, company_name.name AS name, complete_cast.movie_id AS movie_id, complete_cast.subject_id AS subject_id, movie_companies.company_type_id AS company_type_id, movie_companies.note AS note
from comp_cast_type, company_name, complete_cast, movie_companies
 where (comp_cast_type.id = complete_cast.status_id) And (company_name.country_code = '__NOTEQUAL__[pl]')  And  (((company_name.name like '%Film%')) Or ((company_name.name like '%Warner%'))) And (movie_companies.company_id = company_name.id) And (movie_companies.movie_id = complete_cast.movie_id) And (movie_companies.note IS NULL)