create materialized view if not exists mv491 as select comp_cast_type.id AS comp_cast_type_id_0, comp_cast_type.kind AS kind, company_name.country_code AS country_code, company_name.id AS company_name_id_3, company_name.name AS name, complete_cast.movie_id AS movie_id, complete_cast.status_id AS status_id, movie_companies.company_type_id AS company_type_id, movie_companies.note AS note, movie_info.info AS info, movie_info.info_type_id AS info_type_id
 from company_name,complete_cast,movie_info,movie_companies,comp_cast_type
 where (company_name.id = movie_companies.company_id) And (movie_info.movie_id = complete_cast.movie_id) And (complete_cast.subject_id = comp_cast_type.id) And (complete_cast.status_id = comp_cast_type.id) And (movie_companies.movie_id = movie_info.movie_id) And (movie_info.info in ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Danish', 'Norwegian', 'German', 'USA', 'American')) And (movie_companies.note not like '%(USA)%')  And  (movie_companies.note like '%(200%)%') And (comp_cast_type.kind = 'cast') And (comp_cast_type.kind = 'complete')