create materialized view if not exists mv493 as select comp_cast_type.id AS comp_cast_type_id_0, comp_cast_type.kind AS kind, company_name.country_code AS country_code, company_name.id AS company_name_id_3, company_name.name AS name, company_type.id AS company_type_id_5, complete_cast.movie_id AS movie_id, complete_cast.status_id AS status_id, info_type.id AS info_type_id_8, info_type.info AS info_type_info_9, movie_companies.note AS note, movie_info.info AS movie_info_info_11
 from company_name,complete_cast,company_type,movie_info,movie_companies,comp_cast_type,info_type
 where (company_name.id = movie_companies.company_id) And (movie_info.movie_id = complete_cast.movie_id) And (complete_cast.subject_id = comp_cast_type.id) And (complete_cast.status_id = comp_cast_type.id) And (company_type.id = movie_companies.company_type_id) And (movie_companies.movie_id = movie_info.movie_id) And (movie_info.info in ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Danish', 'Norwegian', 'German', 'USA', 'American')) And (info_type.id = movie_info.info_type_id) And (movie_companies.note not like '%(USA)%')  And  (movie_companies.note like '%(200%)%') And (comp_cast_type.kind = 'cast') And (comp_cast_type.kind = 'complete')