create materialized view if not exists mv556 as select comp_cast_type.id AS comp_cast_type_id_0, comp_cast_type.kind AS kind, complete_cast.movie_id AS complete_cast_movie_id_2, complete_cast.subject_id AS subject_id, info_type.id AS info_type_id_4, info_type.info AS info_type_info_5, keyword.id AS keyword_id_6, keyword.keyword AS keyword, movie_info.info AS movie_info_info_8, movie_keyword.movie_id AS movie_keyword_movie_id_9, title.title AS title
 from movie_keyword,complete_cast,keyword,title,movie_info,comp_cast_type,info_type
 where (movie_keyword.movie_id = title.id) And (keyword.id = movie_keyword.keyword_id) And (movie_info.movie_id = complete_cast.movie_id) And (complete_cast.status_id = comp_cast_type.id) And (complete_cast.subject_id = comp_cast_type.id) And (title.id = movie_info.movie_id) And (movie_info.info in ('Horror', 'Action', 'Sci-Fi', 'Thriller', 'Crime', 'War')) And (info_type.id = movie_info.info_type_id) And (comp_cast_type.kind = 'cast') And (comp_cast_type.kind = 'completeverified')