create materialized view if not exists mv570 as select info_type.id AS id, info_type.info AS info_type_info_1, movie_companies.company_id AS company_id, movie_companies.note AS note, movie_info.info AS movie_info_info_4
 from movie_companies,info_type,movie_info
 where (movie_companies.movie_id = movie_info.movie_id) And (movie_companies.note like '%(Blu-ray)%') And (movie_info.info_type_id = info_type.id) And (info_type.info = 'genres') And (movie_info.movie_id = movie_companies.movie_id) And (movie_info.info in ('Horror', 'Thriller'))