create materialized view if not exists mv597 as select info_type.id AS id, info_type.info AS info_type_info_1, movie_info_idx.info AS movie_info_idx_info_2, movie_info_idx.movie_id AS movie_id, title.kind_id AS kind_id, title.production_year AS production_year, title.title AS title
 from movie_info_idx,info_type,title
 where (movie_info_idx.info_type_id = info_type.id) And (movie_info_idx.info < '3.0') And (title.id = movie_info_idx.movie_id) And (info_type.info = 'rating')