create materialized view if not exists mv87 as select cast_info.movie_id AS movie_id, cast_info.note AS cast_info_note_1, cast_info.person_id AS person_id, cast_info.role_id AS role_id, movie_companies.company_id AS company_id, movie_companies.note AS movie_companies_note_5, role_type.role AS role
 from movie_companies,role_type,cast_info
 where (cast_info.movie_id = movie_companies.movie_id) And (movie_companies.note like '%(Japan)%')  And  (movie_companies.note not like '%(USA)%') And (cast_info.role_id = role_type.id) And (role_type.role = 'actress') And (cast_info.note = '(voice: English version)')