select comp_cast_type.id AS comp_cast_type_id_0, comp_cast_type.kind AS kind, complete_cast.movie_id AS movie_id, complete_cast.status_id AS status_id, info_type.id AS info_type_id_4, info_type.info AS info_type_info_5, movie_info.info AS movie_info_info_6, title.production_year AS production_year, title.title AS title
 from complete_cast,title,movie_info,comp_cast_type,info_type
 where (complete_cast.subject_id = comp_cast_type.id) And (title.id = complete_cast.movie_id) And (movie_info.movie_id = title.id) And (movie_info.info_type_id = info_type.id) And (movie_info.info IS NOT NULL)  And  (((movie_info.info = '__LIKE__Japan:%200%')) Or ((movie_info.info = '__LIKE__USA:%200%'))) And (comp_cast_type.kind = 'cast') And (info_type.info = 'release dates')