select cast_info.movie_id AS movie_id, cast_info.person_id AS person_id, keyword.id AS id, keyword.keyword AS keyword, name.name AS name, title.production_year AS production_year, title.title AS title
 from name,movie_keyword,cast_info,keyword,title
 where (name.id = cast_info.person_id) And (movie_keyword.movie_id = title.id) And (keyword.id = movie_keyword.keyword_id) And (cast_info.movie_id = title.id) And (title.production_year > 2014)