create materialized view if not exists mv315 as select cast_info.movie_id AS movie_id, cast_info.person_id AS person_id, cast_info.person_role_id AS person_role_id, char_name.name AS char_name_name_3, comp_cast_type.id AS comp_cast_type_id_4, comp_cast_type.kind AS comp_cast_type_kind_5, complete_cast.subject_id AS subject_id, keyword.id AS keyword_id_7, keyword.keyword AS keyword, kind_type.id AS kind_type_id_9, kind_type.kind AS kind_type_kind_10, name.name AS name_name_11, title.production_year AS production_year, title.title AS title
 from name,movie_keyword,complete_cast,cast_info,keyword,title,kind_type,char_name,comp_cast_type
 where (name.id = cast_info.person_id) And (movie_keyword.movie_id = title.id) And (keyword.id = movie_keyword.keyword_id) And (complete_cast.status_id = comp_cast_type.id) And (complete_cast.subject_id = comp_cast_type.id) And (title.id = complete_cast.movie_id) And (cast_info.movie_id = title.id) And (char_name.id = cast_info.person_role_id) And (kind_type.id = title.kind_id) And (comp_cast_type.kind like '%complete%') And (comp_cast_type.kind = 'cast')