select company_name.country_code AS country_code, company_name.id AS company_name_id_1, company_name.name AS name, company_type.id AS company_type_id_3, company_type.kind AS company_type_kind_4, info_type.id AS info_type_id_5, info_type.info AS info_type_info_6, kind_type.id AS kind_type_id_7, kind_type.kind AS kind_type_kind_8, movie_companies.movie_id AS movie_id, movie_info.info AS movie_info_info_10, movie_info.info_type_id AS info_type_id, movie_info_idx.info AS movie_info_idx_info_12, title.title AS title
 from company_name,company_type,title,movie_info_idx,kind_type,movie_info,movie_companies,info_type
 where (company_name.id = movie_companies.company_id) And (movie_companies.company_type_id = company_type.id) And (company_type.kind = 'production companies') And (title.id = movie_info.movie_id) And (kind_type.id = title.kind_id) And (movie_companies.movie_id = movie_info_idx.movie_id) And (movie_info_idx.info_type_id = info_type.id) And (movie_info.movie_id = movie_companies.movie_id) And (movie_info.info_type_id = info_type.id) And (info_type.info = 'rating') And (info_type.info = 'release dates')