create materialized view if not exists mv270 as select aka_name.person_id AS person_id, cast_info.movie_id AS movie_id, cast_info.note AS cast_info_note_2, cast_info.person_role_id AS person_role_id, cast_info.role_id AS role_id, company_name.country_code AS country_code, company_name.id AS id, movie_companies.note AS movie_companies_note_7, title.production_year AS production_year, title.title AS title
from aka_name, cast_info, char_name, company_name, movie_companies, title
 where (aka_name.person_id = cast_info.person_id) And (cast_info.movie_id = movie_companies.movie_id) And (char_name.id = cast_info.person_role_id) And (company_name.id = movie_companies.company_id) And (movie_companies.movie_id = title.id) And (title.id = cast_info.movie_id) And (title.production_year >= 2007)  And  (title.production_year <= 2008)  And  (title.title like '%Kung%Fu%Panda%')