create materialized view if not exists mv32 as select company_type.kind AS kind, movie_companies.movie_id AS movie_companies_movie_id_1, movie_companies.note AS note, movie_info.info AS info, movie_info.movie_id AS movie_info_movie_id_4, title.production_year AS production_year, title.title AS title
from company_type, info_type, movie_companies, movie_info, title
 where (company_type.id = movie_companies.company_type_id) And (company_type.kind = 'production companies') And (info_type.id = movie_info.info_type_id) And (movie_companies.company_type_id = company_type.id) And (movie_companies.note not like '%(TV)%')  And  (movie_companies.note like '%(USA)%') And (movie_info.info_type_id = info_type.id) And (movie_info.movie_id = title.id) And (title.id = movie_companies.movie_id)