create materialized view if not exists mv397 as select comp_cast_type.id AS comp_cast_type_id_0, comp_cast_type.kind AS kind, complete_cast.movie_id AS movie_id, complete_cast.subject_id AS subject_id, link_type.id AS link_type_id_4, link_type.link AS link
from comp_cast_type, complete_cast, link_type, movie_link
 where (comp_cast_type.id = complete_cast.status_id) And (complete_cast.movie_id = movie_link.movie_id) And (link_type.link like '%follow%') And (movie_link.link_type_id = link_type.id)