create materialized view if not exists mv63 as select cast_info.movie_id AS movie_id, cast_info.note AS cast_info_note_1, cast_info.person_id AS person_id, cast_info.role_id AS role_id, company_name.country_code AS country_code, company_name.id AS id, movie_companies.note AS movie_companies_note_6, title.production_year AS production_year, title.title AS title
from cast_info, company_name, movie_companies, title
 where (cast_info.movie_id = movie_companies.movie_id) And (company_name.id = movie_companies.company_id) And (movie_companies.note like '%(Japan)%')  And  (movie_companies.note not like '%(USA)%')  And  (((movie_companies.note like '%(2006)%')) Or ((movie_companies.note like '%(2007)%'))) And (title.id = cast_info.movie_id) And (title.id = movie_companies.movie_id)