select info_type.id AS id, info_type.info AS info, movie_companies.company_id AS company_id, movie_companies.company_type_id AS company_type_id, movie_companies.movie_id AS movie_id, title.production_year AS production_year, title.title AS title
from info_type, movie_companies, movie_info_idx, title
 where (info_type.info = 'bottom 10 rank') And (movie_companies.movie_id = movie_info_idx.movie_id) And (movie_info_idx.info_type_id = info_type.id) And (title.id = movie_companies.movie_id) And (title.id = movie_info_idx.movie_id)