select info_type.id AS id, info_type.info AS info_type_info_1, kind_type.kind AS kind, movie_companies.company_id AS company_id, movie_companies.company_type_id AS company_type_id, movie_companies.movie_id AS movie_id, movie_info_idx.info AS movie_info_idx_info_6, title.title AS title
from info_type, kind_type, movie_companies, movie_info_idx, title
 where (info_type.info = 'rating') And (kind_type.id = title.kind_id) And (movie_companies.movie_id = title.id) And (movie_info_idx.info_type_id = info_type.id) And (title.id = movie_info_idx.movie_id) And (title.kind_id = kind_type.id)