select company_name.country_code AS country_code, company_name.id AS company_name_id_1, keyword.id AS keyword_id_2, keyword.keyword AS keyword, movie_companies.movie_id AS movie_id, title.production_year AS production_year, title.title AS title
from company_name, keyword, movie_companies, movie_keyword, title
 where (company_name.id = movie_companies.company_id) And (keyword.keyword in ('hero', 'martial-arts', 'hand-to-hand-combat')) And (movie_companies.movie_id = movie_keyword.movie_id) And (movie_companies.movie_id = title.id) And (movie_keyword.keyword_id = keyword.id) And (title.id = movie_companies.movie_id) And (title.id = movie_keyword.movie_id)