select aka_name.person_id AS person_id, cast_info.movie_id AS movie_id, cast_info.note AS note, cast_info.person_role_id AS person_role_id, cast_info.role_id AS role_id, company_name.country_code AS country_code, company_name.id AS id, company_name.name AS company_name_name_7, name.gender AS gender, name.name AS name_name_9, title.production_year AS production_year, title.title AS title
from aka_name, cast_info, company_name, movie_companies, name, title
 where (aka_name.person_id = cast_info.person_id) And (cast_info.movie_id = movie_companies.movie_id) And (company_name.country_code = '[us]')  And  (company_name.name = 'DreamWorks Animation') And (movie_companies.company_id = company_name.id) And (name.id = aka_name.person_id) And (name.id = cast_info.person_id) And (title.id = cast_info.movie_id) And (title.id = movie_companies.movie_id)