select cast_info.movie_id AS movie_id, cast_info.note AS note, cast_info.person_id AS person_id, keyword.id AS id, keyword.keyword AS keyword, movie_info.info AS info, movie_info.info_type_id AS info_type_id, title.production_year AS production_year, title.title AS title
from cast_info, keyword, movie_info, movie_keyword, title
 where (cast_info.movie_id = movie_keyword.movie_id) And (keyword.keyword in ('murder', 'blood', 'gore', 'death', 'female-nudity')) And (movie_info.movie_id = movie_keyword.movie_id) And (movie_keyword.keyword_id = keyword.id) And (title.id = cast_info.movie_id) And (title.id = movie_info.movie_id) And (title.id = movie_keyword.movie_id)