select cast_info.movie_id AS movie_id, cast_info.note AS cast_info_note_1, cast_info.person_id AS person_id, keyword.id AS id, keyword.keyword AS keyword, movie_companies.company_id AS company_id, movie_companies.note AS movie_companies_note_6, title.production_year AS production_year, title.title AS title
from cast_info, keyword, movie_companies, movie_keyword, title
 where (cast_info.movie_id = movie_keyword.movie_id) And (keyword.keyword in ('murder', 'violence', 'blood', 'gore', 'death', 'female-nudity', 'hospital')) And (movie_companies.movie_id = movie_keyword.movie_id) And (movie_keyword.keyword_id = keyword.id) And (title.id = cast_info.movie_id) And (title.id = movie_companies.movie_id) And (title.id = movie_keyword.movie_id)