select company_name.country_code AS country_code, company_name.id AS id, movie_companies.movie_id AS movie_id, movie_companies.note AS note, title.production_year AS production_year, title.title AS title
from company_name, movie_companies, title
 where (company_name.country_code = '[us]') And (movie_companies.company_id = company_name.id) And (movie_companies.note like '%(200%)%')  And  (((movie_companies.note like '%(USA)%')) Or ((movie_companies.note like '%(worldwide)%'))) And (title.id = movie_companies.movie_id) And (title.production_year >= 2007)  And  (title.production_year <= 2010)