create materialized view if not exists mv107 as select company_name.country_code AS country_code, company_name.id AS id, movie_companies.company_type_id AS company_type_id, movie_companies.movie_id AS movie_id, title.production_year AS production_year, title.title AS title
from company_name, movie_companies, title
 where (company_name.country_code = '[ru]') And (movie_companies.company_id = company_name.id) And (title.id = movie_companies.movie_id)