create materialized view if not exists mv167 as select company_type.id AS company_type_id_0, company_type.kind AS company_type_kind_1, info_type.id AS info_type_id_2, info_type.info AS info_type_info_3, kind_type.id AS kind_type_id_4, kind_type.kind AS kind_type_kind_5, movie_companies.company_id AS company_id, movie_companies.movie_id AS movie_id, movie_info_idx.info AS movie_info_idx_info_8, title.title AS title
from company_type, info_type, kind_type, movie_companies, movie_info_idx, title
 where (company_type.kind = 'production companies') And (info_type.info = 'rating') And (kind_type.kind = 'movie') And (movie_companies.company_type_id = company_type.id) And (movie_companies.movie_id = title.id) And (movie_info_idx.info_type_id = info_type.id) And (title.id = movie_info_idx.movie_id) And (title.kind_id = kind_type.id)