select company_type.kind AS company_type_kind_0, info_type.id AS id, info_type.info AS info_type_info_2, kind_type.kind AS kind_type_kind_3, movie_companies.company_id AS company_id, movie_companies.movie_id AS movie_id, movie_info_idx.info AS movie_info_idx_info_6, title.title AS title
from company_type, info_type, kind_type, movie_companies, movie_info_idx, title
 where (company_type.id = movie_companies.company_type_id) And (info_type.info = 'rating') And (kind_type.id = title.kind_id) And (movie_companies.company_type_id = company_type.id) And (movie_companies.movie_id = title.id) And (movie_info_idx.info_type_id = info_type.id) And (title.id = movie_info_idx.movie_id) And (title.kind_id = kind_type.id)