create materialized view if not exists mv70 as select aka_name.name AS aka_name_name_0, aka_name.person_id AS person_id, cast_info.movie_id AS movie_id, cast_info.note AS cast_info_note_3, company_name.country_code AS country_code, company_name.id AS company_name_id_5, movie_companies.note AS movie_companies_note_6, name.name AS name_name_7, role_type.id AS role_type_id_8, role_type.role AS role, title.id AS title_id_10, title.title AS title
 from movie_companies,aka_name,title,name,role_type,company_name,cast_info
 where (movie_companies.movie_id = cast_info.movie_id) And (movie_companies.company_id = company_name.id) And (movie_companies.note like '%(Japan)%')  And  (movie_companies.note not like '%(USA)%') And (aka_name.person_id = name.id) And (title.id = cast_info.movie_id) And (name.id = cast_info.person_id) And (cast_info.role_id = role_type.id) And (role_type.role = 'actress') And (company_name.country_code = '[jp]') And (cast_info.note = '(voice: English version)')