select info_type.id AS id, info_type.info AS info_type_info_1, movie_info.info AS movie_info_info_2, movie_info.movie_id AS movie_id, movie_info_idx.info AS movie_info_idx_info_4, title.title AS title
 from info_type,movie_info_idx,title,movie_info
 where (info_type.id = movie_info.info_type_id) And (movie_info_idx.info_type_id = info_type.id) And (info_type.info = 'genres') And (info_type.info = 'votes') And (title.id = movie_info_idx.movie_id) And (movie_info.movie_id = movie_info_idx.movie_id) And (movie_info.movie_id = title.id) And (movie_info.info in ('Horror', 'Action', 'Sci-Fi', 'Thriller', 'Crime', 'War'))