create materialized view if not exists mv120 as select cast_info.movie_id AS movie_id, movie_companies.company_id AS company_id, movie_info.info_type_id AS info_type_id
 from title,movie_info,cast_info,movie_companies
 where (title.id = movie_info.movie_id) And (cast_info.movie_id = title.id) And (movie_companies.movie_id = title.id) And (movie_info.movie_id = title.id) And ((movie_info.info_type_id > 9) Or (movie_info.info_type_id = 2)) And (title.id = movie_companies.movie_id) And (movie_companies.company_id < 27)