create materialized view if not exists mv121 as select cast_info.movie_id AS cast_info_movie_id_0, cast_info.person_id AS person_id, movie_keyword.movie_id AS movie_keyword_movie_id_2, title.kind_id AS kind_id, title.production_year AS production_year
 from movie_keyword,title,cast_info,movie_info_idx
 where (movie_keyword.movie_id = title.id) And (cast_info.movie_id = title.id) And (movie_info_idx.movie_id = title.id) And (title.kind_id = 7)  And  (title.production_year = 1992) And (cast_info.person_id < 1816925)