create materialized view if not exists mv130 as select movie_companies.company_id AS company_id, movie_companies.company_type_id AS company_type_id, movie_companies.movie_id AS movie_id, movie_info.info_type_id AS info_type_id, title.kind_id AS kind_id
 from title,movie_info,movie_companies
 where (title.id = movie_companies.movie_id) And (movie_info.movie_id = title.id) And (title.kind_id < 7) And (movie_info.info_type_id < 2) And (movie_companies.company_id < 11471)  And  (movie_companies.company_type_id = 1)