create materialized view if not exists mv141 as select movie_info.info_type_id AS movie_info_info_type_id_0, movie_info.movie_id AS movie_id, movie_info_idx.info_type_id AS movie_info_idx_info_type_id_2, title.kind_id AS kind_id, title.production_year AS production_year
 from title,movie_info,movie_info_idx
 where (movie_info.movie_id = title.id) And (movie_info_idx.movie_id = title.id) And (title.kind_id < 4)  And  (title.production_year > 1992) And (movie_info.info_type_id = 1) And (movie_info_idx.info_type_id > 100)