create materialized view if not exists mv24 as select cast_info.movie_id AS movie_id, movie_info.info_type_id AS movie_info_info_type_id_1, movie_info_idx.info_type_id AS movie_info_idx_info_type_id_2, title.production_year AS production_year
 from title,movie_info,cast_info,movie_info_idx
 where (cast_info.movie_id = title.id) And (movie_info.movie_id = title.id) And (movie_info_idx.movie_id = title.id) And (title.production_year < 1966) And (movie_info.info_type_id = 3) And (movie_info_idx.info_type_id < 100)