create materialized view if not exists mv5 as select movie_info.movie_id AS movie_id, movie_info_idx.info_type_id AS info_type_id, movie_keyword.keyword_id AS keyword_id, title.kind_id AS kind_id, title.production_year AS production_year
 from movie_keyword,movie_info,title,movie_info_idx
 where (movie_keyword.keyword_id > 819) And (title.id = movie_keyword.movie_id) And (movie_info.movie_id = title.id) And (movie_info_idx.movie_id = title.id) And (movie_info_idx.info_type_id = 99)