create materialized view if not exists mv59 as select movie_companies.company_id AS company_id, movie_companies.company_type_id AS company_type_id, movie_companies.movie_id AS movie_id, title.kind_id AS kind_id, title.production_year AS production_year
 from title,movie_companies
 where (movie_companies.movie_id = title.id) And (((title.kind_id < 7)  And  (title.production_year = 2006)) Or ((title.kind_id = 7)  And  (title.production_year = 2004))) And (movie_companies.company_id < 79751)  And  (movie_companies.company_type_id > 1)