create materialized view if not exists mv97 as select cast_info.movie_id AS movie_id, cast_info.person_id AS person_id, cast_info.role_id AS role_id, movie_companies.company_id AS company_id, movie_keyword.keyword_id AS keyword_id
 from movie_keyword,title,cast_info,movie_companies
 where (movie_keyword.movie_id = title.id) And (movie_keyword.keyword_id < 4030) And (cast_info.movie_id = title.id) And (movie_companies.movie_id = title.id) And (cast_info.person_id > 607862)  And  (cast_info.role_id < 2) And (movie_companies.company_id > 2561)