select movie_info_idx.movie_id AS movie_id, movie_keyword.keyword_id AS keyword_id, title.kind_id AS kind_id, title.production_year AS production_year
 from movie_keyword,title,movie_info_idx
 where (movie_keyword.movie_id = title.id) And (movie_keyword.keyword_id < 78213) And (movie_info_idx.movie_id = title.id) And (((title.kind_id < 7)  And  (title.production_year = 1998)) Or ((title.kind_id = 7)  And  (title.production_year = 2001)))