select cast_info.movie_id AS movie_id, cast_info.person_id AS person_id, cast_info.role_id AS role_id, movie_info.info_type_id AS info_type_id
 from title,movie_info,cast_info,movie_companies
 where (title.id = movie_info.movie_id) And (cast_info.movie_id = title.id) And (movie_companies.movie_id = title.id) And (movie_info.movie_id = title.id) And ((movie_info.info_type_id > 9) Or (movie_info.info_type_id = 2)) And (cast_info.person_id > 1021231)  And  (cast_info.role_id < 10)